import React, { useState, useEffect } from 'react';

const API_PORT = 3000;
const API_BASE_URL = `http://localhost:${API_PORT}`;

const App: React.FC = () => {
    const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
    const [tasks, setTasks] = useState<{ taskid: number; title: string; description: string; completed: boolean }[]>([]);
    const [newTitle, setNewTitle] = useState('');
    const [newDescription, setNewDescription] = useState('');
    const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
    const [editedTitle, setEditedTitle] = useState('');
    const [editedDescription, setEditedDescription] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        if (token) fetchTasks();
    }, [token]);

    const fetchTasks = async () => {
        if (!token) return;
        try {
            const response = await fetch(`${API_BASE_URL}/tasks`, {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` },
            });
            if (!response.ok) throw new Error('Failed to fetch tasks');
            const data = await response.json();
            setTasks(data);
        } catch (err: any) {
            setError(err.message);
        }
    };

    const editTask = async (taskId: number) => {
        if (!token) return;
        try {
            const response = await fetch(`${API_BASE_URL}/tasks/${taskId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ title: editedTitle, description: editedDescription }),
            });
            if (!response.ok) throw new Error('Failed to update task');
            setEditingTaskId(null);
            fetchTasks();
        } catch (err: any) {
            setError(err.message);
        }
    };

    return token ? (
        <div>
            <h2>Your Tasks</h2>
            <ul>
                {tasks.map(task => (
                    <li key={task.taskid}>
                        {editingTaskId === task.taskid ? (
                            <>
                                <input type="text" value={editedTitle} onChange={(e) => setEditedTitle(e.target.value)} />
                                <input type="text" value={editedDescription} onChange={(e) => setEditedDescription(e.target.value)} />
                                <button onClick={() => editTask(task.taskid)}>Confirm</button>
                                <button onClick={() => setEditingTaskId(null)}>Cancel</button>
                            </>
                        ) : (
                            <>
                                <strong>{task.title}</strong>
                                <p>{task.description}</p>
                                <button onClick={() => { setEditingTaskId(task.taskid); setEditedTitle(task.title); setEditedDescription(task.description); }}>Edit</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    ) : (
        <div>
            <h2>Log In</h2>
            <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default App;
