import React, { useState } from 'react';
import axios from 'axios';

const Login: React.FC = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleLogin = async () => {
        try {
            console.log(username);
            console.log(password);
            const response = await axios.post('http://localhost:3000/login', { username, password });
            setSuccessMessage(`Welcome back, ${response.data.username}!`);
            setError('');
            // Handle successful login (e.g., redirect, store token, etc.)
        } catch (err) {
            setError('Login failed. Please check your credentials.');
            setSuccessMessage('');
        }
    };

    const handleSignUp = async () => {
        try {
            const response = await axios.post('http://localhost:3000/register', { username, password });
            setSuccessMessage(`Registration successful! Welcome, ${response.data.username}!`);
            setError('');
            // Handle successful registration (e.g., redirect, auto-login, etc.)
        } catch (err) {
            setError('Registration failed. Please try again.');
            setSuccessMessage('');
        }
    };

    return (
        <div style={{ maxWidth: '400px', margin: 'auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <h2>Log In / Sign Up</h2>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                style={{ width: '100%', marginBottom: '10px', padding: '8px' }}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{ width: '100%', marginBottom: '10px', padding: '8px' }}
            />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            <button onClick={handleLogin} style={{ marginRight: '10px' }}>Log In</button>
            <button onClick={handleSignUp}>Sign Up</button>
        </div>
    );
};

export default Login;
