import React, { useState, useEffect } from 'react';

const App: React.FC = () => {
    const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
    const [tasks, setTasks] = useState<{ taskid: number; title: string; description: string; completed: boolean }[]>([]);
    const [newTitle, setNewTitle] = useState('');
    const [newDescription, setNewDescription] = useState('');
    const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
    const [editedTitle, setEditedTitle] = useState('');
    const [editedDescription, setEditedDescription] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        if (token) fetchTasks();
    }, [token]);

    const handleAuth = async (endpoint: string) => {
        try {
            const response = await fetch(`http://localhost:3000/${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) throw new Error(`${endpoint} failed`);

            const data = await response.json();
            localStorage.setItem('token', data.token);
            setToken(data.token);
            setError('');
        } catch (err: any) {
            setError(err.message);
        }
    };

    const fetchTasks = async () => {
        if (!token) return;
        try {
            const response = await fetch('http://localhost:3000/tasks', {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (!response.ok) throw new Error('Failed to fetch tasks');
            const data = await response.json();
            setTasks(data);
        } catch (err: any) {
            setError(err.message);
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setToken(null);
    };

    if (token) {
        return (
            <div>
                <button onClick={logout} style={{ position: 'absolute', top: '10px', right: '10px' }}>
                    Logout
                </button>
                <h2>Your Tasks</h2>
                <div>
                    <input
                        type="text"
                        placeholder="Title"
                        value={newTitle}
                        onChange={(e) => setNewTitle(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Description"
                        value={newDescription}
                        onChange={(e) => setNewDescription(e.target.value)}
                    />
                    <button onClick={fetchTasks}>Refresh</button>
                </div>
                <ul>
                    {tasks.length > 0 ? (
                        tasks.map(task => (
                            <li key={task.taskid} style={{ marginBottom: '60px' }}>
                                <strong>{task.title}</strong>
                                <p>{task.description}</p>
                            </li>
                        ))
                    ) : (
                        <p>No tasks found.</p>
                    )}
                </ul>
            </div>
        );
    }

    return (
        <div>
            <h2>Log In / Sign Up</h2>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <button onClick={() => handleAuth('login')}>Log In</button>
            <button onClick={() => handleAuth('register')}>Sign Up</button>
        </div>
    );
};

export default App;
