import React, { useState, useEffect } from 'react';

const App: React.FC = () => {
    const [userId, setUserId] = useState<number | null>(null);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [tasks, setTasks] = useState<{ taskid: number; title: string; description: string; completed: boolean }[]>([]);
    const [newTitle, setNewTitle] = useState('');
    const [newDescription, setNewDescription] = useState('');
    const [error, setError] = useState('');
    
    useEffect(() => {
        if (userId) {
            fetchTasks();
        }
    }, [userId]);

    const handleAuth = async (endpoint: string) => {
        try {
            const response = await fetch(`http://localhost:3000/${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password })
            });
            
            if (!response.ok) throw new Error(`${endpoint} failed`);
            
            const data = await response.json();
            setUserId(data.userId);
            setError('');
        } catch (err: any) {
            setError(err.message);
        }
    };

    const fetchTasks = async () => {
        try {
            console.log("Fetching tasks for user:", userId);
            const response = await fetch(`http://localhost:3000/tasks/${userId}`);
            console.log("Response status:", response.status);
            if (!response.ok) throw new Error('Failed to fetch tasks');
            const data = await response.json();
            console.log("Fetched tasks:", data);
            setTasks(data);
        } catch (err: any) {
            setError(err.message);
        }
    };
    

    const addTask = async () => {
        try {
            const response = await fetch('http://localhost:3000/create', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userId, title: newTitle, description: newDescription })
            });
            
            if (!response.ok) throw new Error('Failed to add task');
            
            setNewTitle('');
            setNewDescription('');
            fetchTasks();
        } catch (err: any) {
            setError(err.message);
        }
    };


    const deleteTask = async (taskId: number) => {
        try {
            const response = await fetch(`http://localhost:3000/tasks/${taskId}`, {
                method: 'DELETE'
            });
            if (!response.ok) throw new Error('Failed to delete task');
            setTasks(tasks.filter(task => task.taskid !== taskId));
        } catch (err: any) {
            setError(err.message);
        }
    };


    const toggleTaskCompletion = async (taskId: number) => {
        try {
            const response = await fetch(`http://localhost:3000/tasks/${taskId}/toggle`, {
                method: 'PATCH'
            });
            if (!response.ok) throw new Error('Failed to toggle task completion');
            fetchTasks(); // Refresh the task list after toggling
        } catch (err: any) {
            setError(err.message);
        }
    };

    if (userId) {
        return (
            <div>
                <h2>Your Tasks</h2>
                <div>
                    <input 
                        type="text" 
                        placeholder="Title" 
                        value={newTitle} 
                        onChange={(e) => setNewTitle(e.target.value)}
                    />
                    <input 
                        type="text" 
                        placeholder="Description" 
                        value={newDescription} 
                        onChange={(e) => setNewDescription(e.target.value)}
                    />
                    <button onClick={addTask}>+</button>
                </div>
                <ul>
                    {tasks.length > 0 ? (
                        tasks.map(task => (
                            <li key={task.taskid} style={{ marginBottom: '60px' }}>
                        <strong>{task.title}</strong>
                        <p>{task.description}</p>
                        <button onClick={() => toggleTaskCompletion(task.taskid)}>
                                    {task.completed ? 'Undo' : 'Complete'}
                                </button>
                        <button onClick={() => deleteTask(task.taskid)}>Delete</button>
                    </li>
                        ))
                    ) : (
                        <p>No tasks found.</p>
                        )}
                </ul>

            </div>
        );
    }

    return (
        <div>
            <h2>Log In / Sign Up</h2>
            <input 
                type="text" 
                placeholder="Username" 
                value={username} 
                onChange={(e) => setUsername(e.target.value)}
            />
            <input 
                type="password" 
                placeholder="Password" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)}
            />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <button onClick={() => handleAuth('login')}>Log In</button>
            <button onClick={() => handleAuth('register')}>Sign Up</button>
        </div>
    );
};

export default App;
